import React, { useState } from "react";
import "../component/roast.css";

const emojis = ["沽", "沽ｮ", "沽｡", "洟｢", "洟能

\");\n const [newRoast, setNewRoast] =:
");
    }
  };

  const handleLikeDislike = (movieId, roastId, action) => {
    const updateMovies = movies.map(movie => {
      if (movie.id === movieId) {
        const updateRoasts = movie.roasts.map((roast) => {
          if (roast.id == roastId) {
            return { ...roast, [action]: roast[action] + 1 };
          }
          return roast;
        });
        return { ...movies, roasts: updateRoasts };
      }
      return movies;
    });
    setMovies(updateMovies);
  };

  const handleAddComment = (movieId, roastId) => {
    if (newComment.trim()) {
      const updateMovies = movies.map(movie => {
        if (movie.id == movieId) {
          const updateRoasts = movie.roast.map((roast) => {
            if (roast.id === roastId) {
              return { ...roast, comments: [...roast.comments, newComment] };
            }
            return roast;
          });
          return { ...movies, roasts: updateRoasts };
        }
        return movies;
      });
      setMovies(updateMovies);
      setNewComment("");
    }
  };

  const handleReaction = (movieId, roastId, emoji) => {
    const updateMovies = movies.map((movie) => {
      if (movie.id == movieId) {
        const updateRoasts = movie.roast.map(roast => {
          if (roast.id === roastId) {
            const updatedReaction = {
              ...roast.reactions,
              [emoji]: roast.reactions[emoji] + 1,
            };
            return { ...roast, reactions: updatedReaction };
          }
          return roast;
        });
        return { ...movie, roast: updateRoasts};
      }
      return movie;
    });
    setMovies(updateMovies);
  };

  return (
  <div className="movie-roast">
    <h1>沚ｿ Movie Roast Central 沐･</h1>
    <div className="add-movie">
      <input
        type="text"
        value={newMovie}
        onChange={(e) => setNewMovie(e.target.value)}
        placeholder="Enter a movie title"
      />
      <button onClick={handleAddMovie}>Add Movie</button>
    </div>
    <div className="movie-list">
      {movies.map(movie => (
        <div
          key={movie.id}
          className={`movie-card ${selectedMovie && selectedMovie.id === movie.id ? 'selected' : ''}`}
          onClick={() => setSelectedMovie(movie)}
        >
          <h2>{movie.title}</h2>
          <p>{movie.roasts.length} roasts</p>
        </div>
      ))}
    </div>

    {selectedMovie && (
      <div className="roast-section">
        <h2>Roast "{selectedMovie.title}"</h2>
        <div className="roast-list">
          {selectedMovie.roasts.map((roast) => (
            <div key={roast.id} className="roast-card">
              <p>"{roast.text}"</p>
              <div className="roast-actions">
                <button onClick={() => handleLikeDislike(selectedMovie.id, roast.id, 'likes')}>
                  汨 {roast.likes}
                </button>
                <button onClick={() => handleLikeDislike(selectedMovie.id, roast.id, 'dislikes')}>
                  汨 {roast.dislikes}
                </button>
                {emojis.map(emoji => (
                  <button key={emoji} onClick={() => handleReaction(selectedMovie.id, roast.id, emoji)}>
                    {emoji} {roast.reactions[emoji]}
                  </button>
                ))}
              </div>
              <div className="comments-section">
                <h4>Comments:</h4>
                {roast.comments.map((comment, index) => (
                  <p key={index}>{comment}</p>
                ))}
                <input
                  type="text"
                  value={newComment}
                  onChange={(e) => setNewComment(e.target.value)}
                  placeholder="Add a comment"
                />
                <button onClick={() => handleAddComment(selectedMovie.id, roast.id)}>
                  Post Comment
                </button>
              </div>
            </div>
          ))}
        </div>
        <div className="add-roast">
          <input
            type="text"
            value={newRoast}
            onChange={(e) => setNewRoast(e.target.value)}
            placeholder="Enter your roast"
          />
          <button onClick={handleAddRoast}>Add Roast</button>
        </div>
      </div>
    )}
  </div>
);
};

