import React, { useState } from "react";
import '../component/roast.css'
import { title } from "process";

const emojis = ['üòÇ', 'üòÆ', 'üò°', 'ü§¢', 'ü§î'];

export const MovieRoast = () => {
    const [movies, setMovies] = useState([
        {
            id: 1,
            title: 'The Room',
            roast: ['fuck this movie'],
            likes: 0,
            dislikes: 0,
            comments: [],
            reactions: { 'üòÇ': 0, 'üòÆ': 0, 'üò°': 0, 'ü§¢': 0, 'ü§î': 0 },
        },
        {
            id: 2,
            title: 'Cats',
            roast: [
                {
                    id: 1,
                    text: 'A big dick',
                    likes: 0,
                    dislikes: 0,
                    comments: [],
                    reactions: { 'üòÇ': 0, 'üòÆ': 0, 'üò°': 0, 'ü§¢': 0, 'ü§î': 0 },
                }
            ]
        },
    ]);


    const [newMovie, setNewMovies] = useState('');
    const [newRoast, setNewRoast] = useState('');
    const [selectMovie, setSelectMovie] = useState('');
    const [newComment, setNewComment] = useState('');

    const handleAddMovie = () => {
        if (newMovie.trim()) {
            setMovies([...movies, { id: Date.now(), title: newMovie, roast: [] }]);
            setNewMovies('');
        }
    };

    const handleAddRoast = () => {
        if (newRoast.trim() && selectMovie) {
            const updateMovies = movies.map(movie =>
                movie.id === selectMovie.id
                    ? {
                        ...movie, roast: [...movie.roast, {
                            id: Date.now(),
                            text: newRoast,
                            likes: 0,
                            dislikes: 0,
                            comments: [],
                            reactions: { 'üòÇ': 0, 'üòÆ': 0, 'üò°': 0, 'ü§¢': 0, 'ü§î': 0 },
                        }]
                    } : movie
            );
            setMovies(updateMovies);
            setNewRoast('');
        }
    };

    const handleLikeDislike = (movieId, roastId, action) => {
        const updateMovies = movies.map(movies => {
            if (movies.id === movieId) {
                const updateRoasts = movies.roast.map(roast => {
                    // return { ...roast, [action]: roast[action] + 1 };
                    if (roast.id == roastId) {
                        return { ...roast, [action]: roast[action] + 1 };
                    }
                    return roast;
                });
                return { ...movies, roasts: updateRoasts }
            }
            return movies;
        });
        setMovies(updateMovies);
        // return {...movies,roasts:updateRoasts};
    };

    const handleAddComment = (movieId, roastId) => {
        if (newComment.trim()) {
            const updateMovies = movies.map(movies => {
                if (movies.id == movieId) {
                    const updateRoasts = movies.roast.map(roast => {
                        if (roast.id === roastId) {
                            return { ...roast, comments: [...roast.comments, newComment] };
                        }
                        return roast;
                    });
                    return { ...movies, roasts: updateRoasts };
                }
                return movies;
            });
            setMovies(updateMovies);
            setNewComment('');
        }
    };


    const handleReaction = (movieId, roastId, emoji) => {
        const updateMovies = movies.map(movie => {
            if (movie.id == movieId) {
                const updateRoasts = movie.roast.map(roast => {
                    if (roast.id === roastId) {
                        const updatedReaction = { ...roast.reactions, [emoji]: roast.reactions[emoji] + 1 };
                        return { ...roast, reactions: updatedReaction };
                    }
                    return roast;
                });
                return { ...movie, roast: updateMovies };
            }
            return movie;
        });
        setMovies(updateMovies);
    };

    return (
        <div className="movie-roast">
            <h1>üçø Movie Roast Central üî•</h1>
            <div className="add-movie">
                <input
                    type="text"
                    value={newMovie}
                    onChange={(e) => setNewMovies(e.target.value)}
                    placeholder="Enter a movie title"
                />
                <button onClick={handleAddMovie}>Add Movie</button>
            </div>
            <div className="movie-list">
                {movies.map(movie => {
                    <div
                        key={movie.id}
                        className={`movie.card ${selectMovie && selectMovie.id === movie.id ? 'selected' : ''}`}
                        onClick={() => setSelectMovie(movie)}
                    >
                        <h2>{movie.title}</h2>
                        <p>{movie.roast.length} roast</p>
                    </div>
                })}
            </div>

            {selectMovie && (
                <div className="roast-section">
                    <h2>Roast "{selectMovie.title}"</h2>
                    <div className="roast-list">
                        {selectMovie.roast.map((roast) => {
                            <div key={roast.id} className="roast-card">
                                <p>"{roast.text}"</p>
                                <div className="roast-action">
                                    <button onClick={() => handleLikeDislike(selectMovie.id, roast.id, 'likes')}>
                                        {roast.likes}
                                    </button>
                                    <button onClick={() => handleLikeDislike(selectMovie.id, roast.id, 'dislike')}>
                                        {roast.dislikes}
                                    </button>
                                    {emojis.map(emojis => {
                                        <button key={emojis} onClick={() => handleReaction(selectMovie.id, roast.id, emojis)}>
                                            {emojis}{roast.reactions[emojis]}
                                        </button>
                                    })}
                                    <div>
                                        <div className="comments-section">
                                            <h4>Comments:</h4>
                                            {roast.comments.map((Comment, index) => {
                                                <p key={index}>{Comment}</p>
                                            })}
                                            <input
                                                type="text"
                                                value={newComment}
                                                onChange={(e) => setNewComment(e.target.value)}
                                                placeholder="Add a comment"
                                            />
                                            <button onClick={() => handleAddComment(selectMovie.id, roast.id)}>
                                                Post Comment
                                            </button>
                                        </div>
                                    </div>
                        </div>
                        </div>
                        })}
                        </div>
                        </div>

            )}
        </div>
    );
};

// export default MovieRoast;
